, , . , .

****:

, O(N). , O(N).  $2O(N)=O(N)$

****:

, O(N),  O(max(m, n)) ps.Lmax, Lmax+1,  $O(N)+O(max(m, n))$.

```javascript
// 
// 
var addTwoNumbers = function (l1, l2) {
  // 
  let stack1 = [], stack2 = [];
  while (l1) {
    stack1.push(l1.val);
    l1 = l1.next;
  }
  while (l2) {
    stack2.push(l2.val);
    l2 = l2.next;
  }
  // 
  let sum = 0, carry = 0, num = 0;
  let res = new ListNode(null), rn;
  let add1, add2;
  // , 
  //  0
  while (stack1.length != 0 || stack2.length != 0) {
    add1 = stack1.length == 0 ? 0 : stack1.pop();
    add2 = stack2.length == 0 ? 0 : stack2.pop();
    sum = add1 + add2 + carry;
    //  101,100
    carry = Math.floor(sum / 10);
    // 
    num = sum % 10;
    // 
    rn = res.next;
    res.next = new ListNode(num);
    res.next.next = rn;
  }
  // , 
  // 
  if (carry != 0) {
    rn = res.next;
    res.next = new ListNode(carry);
    res.next.next = rn;
  }
  return res.next;
};
```

